import { useEffect,useState } from 'react';
import classes from './AvailableMeals.module.css';
import Card from '../UI/Card';
import MealItem from './MealItem/MealItem';


  const AvailableMeals = () =>{
const [meals,setMeals] = useState('');

useEffect(() =>{
  const fetchMeals = async () =>{
   const response = await fetch('https://react-http-77b94-default-rtdb.firebaseio.com/meals.json');
   const responseData = await response.json();  //it returns a promise because the await
   //here responseData will be an obj and we want an array so we will transform it

   const loadedMeals = [];

   for(const key in responseData){
    loadedMeals.push({
      id:key,
      name:responseData[key].name,
      description:responseData[key].description,
      price:responseData[key].price,
    });
   };
   setMeals(loadedMeals);
  };
fetchMeals();
},[]
);

const mealsList = meals.map(meal => 
<MealItem
    id={meal.id} // this is new!
    key={meal.id}
    name={meal.name}
    description={meal.description}
    price={meal.price}
/>
     );
    return (
    <section className={classes.meals}>
        <Card>
        <ul>
            {mealsList}
        </ul>
        </Card>
    </section>
);
  };

  export default AvailableMeals;
